    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String userFTP = txtUsuario.getText();
        String passFTP = txtPass.getText();

        if (userFTP == null || userFTP.isBlank()) {
            JOptionPane.showMessageDialog(this, "Usuario no v치lido", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        if (passFTP == null || passFTP.isBlank()) {
            JOptionPane.showMessageDialog(this, "Contrase침a no v치lida", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        vp.setUsuarioFTP(userFTP);
        vp.setPassFTP(passFTP);

        this.dispose();
    }//GEN-LAST:event_jButton1ActionPerformed

